import React, { useEffect, useState} from "react";
import { useParams, Link } from "react-router-dom";

import "bootstrap/dist/css/bootstrap.min.css";

import Navbar from "../../components/Navbar";
import Heading from "../../components/Heading";
import BlogList from "../../components/BlogList";
import Footer from "../../components/Footer";
import Loading from "../../components/Loading";

import categoriesService from "../../services/categoriesService";
import blogService from "../../services/blogService";
import AddEditBlogModal from "../../components/AddEditBlogModal";
import SuccessToast from "../../components/SuccessToast";
import ErrorToast from "../../components/ErrorToast";
import DeleteBlogModal from "../../components/DeleteBlogModal";



import "../../App.css";
import "./index.css";




// Week 1: Import the blogPosts and categories from the dummy-data.json file


export default function BlogsPage() {

  const user = true;

  let { categoryIdPassed } = useParams();
  const [ blogs, setBlogs] = useState();
  const [ categories, setCategories] = useState();

  const [addBlog, setAddBlog] = useState();
  const [editBlog, setEditBlog] = useState();
  const [deleteBlog, setDeleteBlog] = useState();

  const [ loading, setLoading ] = useState(true);
  const [isError, setIsError] = useState(false);
  const [isSuccess, setIsSuccess] = useState(false);
  const [message, setMessage] = useState("");


  const onAddBlog = () => {
    setAddBlog({
      image: "",
      title: "",
      description: "",
      categories: [],
      content: [],
      author: {
        id: 1,
        firstName: "Byron",
        lastName: "de Villiers",
        bio: "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
        image:
          "data:image/png;base64,",
      },
      content: [
        {
          sectionHeader: "Introduction",
          sectionText:
            "I'm so excited to share my first blog post with the world. I've been working on this for a while and I'm happy to finally share it with you.\n\nLorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
        },
      ]  
    });
  };

  const AddBlog = () => {
    return (
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <p className="page-subtitle">Blog Posts</p>
        {user && (
          <button
            style={{ margin: "16px" }}
            type="button"
            className="btn btn-outline-secondary"
            onClick={onAddBlog}
          >
            Add Blog Post
          </button>
        )}
      </div>
    );
  };

  const onBlogEdit = (blog) => {
    setEditBlog(blog);
  }

  const onBlogDelete = (blog) => {
    setEditBlog(blog);
  }

  const createBlog = async (blog) => {
    try {
      const newBlog = await blogService(blog);
      setIsSuccess(true);
      setMessage(newBlog.message);
      setBlogs((prev) => {
        return [...prev, newBlog.data];
      });
    } catch (err) {
      setIsError(true);
      setMessage(err);
    }
    setAddBlog(null);
  }

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        let blogPosts;
        if (!categoryIdPassed) {
          blogPosts = await blogService.fetchBlogs();
        } else {
          blogPosts = await blogService.fetchBlogsByCategoryId(categoryIdPassed);
        }
        
        const categoriesJSON = await categoriesService.fetchCategories();
        setCategories(categoriesJSON.data);
        setBlogs(blogPosts.data);
        setIsSuccess(true);
        setMessage(blogPosts.message);
        setLoading(false);
      } catch (error) {
        setIsError(true);
        setMessage(error.message || error);
        setLoading(false);
      }
    };
    fetchData();
  }, [categoryIdPassed]);

  const resetSuccess = () => {
    setIsSuccess(false);
    setMessage("");
  }

  const resetError = () => {
    setIsError(false);
    setMessage("");
  }



   const CategoriesList = () => {
    

     return categories.map((category) => {
       return categoryIdPassed === category.id.toString() ? (
        
         <Link
           key={category.id}
           to={"/blogs/"+category.id}
           style={{ color: "blue" }}
           onClick={() => setLoading(true)}
         >
           <p key={category.id}>{category.title}</p>
         </Link>
       ) : (
         <Link
           key={category.id}
           to={"/blogs/"+category.id}
           style={{ color: "black" }}
           onClick={() => setLoading(true)}
         >
           <p key={category.id}>{category.title}</p>
         </Link>
       );
     });
  }

   if (loading) {
     return <Loading/>
   }

   return (
     <>
       <Navbar />
       <div className="container">
         <Heading />
         <div className="scroll-menu">
           <CategoriesList categories={categories}/>
         </div>
         <AddBlog />
         {/* <AddEditBlogModal
           addBlog={addBlog}
           editBlog={editBlog}
           categories={categories}
           createBlog={createBlog}
           updateBlog={updateBlog}
         /> */}
         <BlogList blogs={blogs} />
       </div>
       <Footer />
       <SuccessToast
         show={isSuccess}
         message={message}
         onClose={resetSuccess}
       />
       <ErrorToast show={isError} message={message} onClose={resetError} />
     </>
   );
}
