import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

import Navbar from "../../components/Navbar";
import Heading from "../../components/Heading";
import BlogList from "../../components/BlogList";
import Footer from "../../components/Footer";

import AddEditBlogModal from "../../components/AddEditBlogModal";
import Loading from "../../components/Loading";
import SuccessToast from "../../components/SuccessToast";
import ErrorToast from "../../components/ErrorToast";
import DeleteBlogModal from "../../components/DeleteBlogModal";

import "./index.css";

import {
  setAddBlog,
  fetchBlogsByCategoryId,
  resetSuccessAndError as resetBlog,
  deleteBlogById,
} from "../../features/blogsSlice";
import {
  fetchCategories,
  resetSuccessAndError as resetCategory,
} from "../../features/categoriesSlice";



export default function BlogsPage() {
  const user = JSON.parse(localStorage.getItem("user"));

  let { categoryIdPassed } = useParams();

  const dispatch = useDispatch();

  const {
    blogs,
    isError: isBlogsError,
    isSuccess: isBlogSuccess,
    isLoading: isLoadingBlogs,
    message: blogsMessage,
  } = useSelector((state) => state.blogs);
  const {
    categories,
    isError: isCategoriesError,
    isSuccess: isCategoriesSuccess,
    isLoading: isLoadingCategories,
    message: categoriesMessage,
  } = useSelector((state) => state.categories);

  useEffect(() => {
    dispatch(fetchCategories());
    dispatch(fetchBlogsByCategoryId(categoryIdPassed));
    return () => {
      dispatch(resetBlog());
      dispatch(resetCategory());
    };
  }, [categoryIdPassed]);


  const onBlogAdd = () => {
    dispatch(
      setAddBlog({
        title: "",
        description: "default",
        categories: [],
        authorId: user.id,
        content: [
            {
              sectionHeader: "default header",
              sectionText: "default text"
            }
        ]
      })
    );
  };

  const onBlogEdit = (blog) => {
    setEditBlog(blog);
  };

  const onBlogDelete = (blog) => {
    setDeleteBlog(blog);
  };

  const createBlog = async (blog) => {
    setAddBlog(blog);
  };

  const updateBlog = async (blog) => { // to remove
    try {
      dispatch(updateBlog(blog));
    } catch (err) {
      console.log(err);
    }
  };

  const removeBlog = async (blog) => {
    try {
      dispatch(deleteBlogById(blog.id));
    } catch (err) {
      console.log(err);
    }
    setDeleteBlog(null);
  };

  const CategoriesList = ({ categoryId }) => {
    if (!categories && !categories?.length) {
      return null;
    }

    return categories.map((category) => {
      return categoryId === category.id ? (
        <Link
          className="link"
          key={category.id}
          to={"/blogs/" + category.id}
          style={{ color: "blue" }}
        >
          <p key={category.id}>{category.title}</p>
        </Link>
      ) : (
        <Link
          className="link"
          key={category.id}
          to={"/blogs/" + category.id}
          style={{ color: "black" }}
        >
          <p key={category.id}>{category.title}</p>
        </Link>
      );
    });
  };

  if (isLoadingBlogs || isLoadingCategories) {
    return <Loading />;
  }

  return (
    <>
      <Navbar />
      <div className="container">
        <Heading />
        <div className="scroll-menu">
          <CategoriesList categoryId={categoryIdPassed} />
        </div>
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <p className="page-subtitle">Blog Posts</p>
          {user && user.token ? <button className="btn btn-outline-dark h-75" onClick={onBlogAdd}>
            ADD BLOG
          </button> : null}
        </div>
        <BlogList
          blogs={blogs}
        />
        <AddEditBlogModal/>
        <DeleteBlogModal/>
      </div>
      <Footer />
      <SuccessToast
        show={isBlogSuccess || isCategoriesSuccess}
        message={blogsMessage || categoriesMessage}
        onClose={() => {
          dispatch(resetBlog());
          dispatch(resetCategory());
        }}
      />
      <ErrorToast
        show={isBlogSuccess || isCategoriesSuccess}
        message={blogsMessage || categoriesMessage}
        onClose={() => {
          dispatch(resetBlog());
          dispatch(resetCategory());
        }}
      />
    </>
  );
}
