import React from "react";

export default function EditButtons({ onEdit, onDelete, onNavigate, onBlog }) {
  return (
    <>
      <button
        style={onBlog ? {
          backgroundColor: "#141414cc",
          color: "gray",
          margin: "0px 30px",
          position: "absolute",
          top: "10px",
          right: "60px",
          border: "none",
          zIndex: 1,
        } : {
          position: "absolute",
          top: "10px",
          right: "60px",
          border: "none",
          zIndex: 1,
        }}
        type="button"
        className="btn"
        onClick={onEdit}
      >
        <i className="bi bi-pencil-fill"></i>
      </button>
      <button
        style={onBlog ? {
          backgroundColor: "#141414cc",
          color: "gray",
          margin: "0px 15px",
          position: "absolute",
          top: "10px",
          right: "35px",
          border: "none",
          zIndex: 1,
        } : {
          margin: "0px 15px",
          position: "absolute",
          top: "10px",
          right: "35px",
          border: "none",
          zIndex: 1,
        }}
        type="button"
        className="btn"
        onClick={onDelete}
      >
        <i className="bi bi-trash-fill"></i>
      </button>
      <button
        style={{
          backgroundColor: "#141414cc",
          color: "gray",
          margin: "0px 0px",
          position: "absolute",
          top: "10px",
          right: "10px",
          border: "none",
          zIndex: 1,
        }}
        type="button"
        className="btn"
        onClick={onNavigate}
      >
      <i className="bi bi-arrows-fullscreen"></i>
    </button>
    </>
  );
}
