import React, { useEffect, useState } from "react";

import { useDispatch, useSelector } from "react-redux";


import Navbar from "../../components/Navbar";
import Heading from "../../components/Heading";
import BlogGrid from "../../components/BlogGrid";
import CategoriesList from "../../components/CategoriesList";
import Footer from "../../components/Footer";
import SubHeading from "../../components/SubHeading";
import Loading from "../../components/Loading";

// import blogService from "../../services/blogService";
import categoryService from "../../services/categoriesService";

import { fetchBlogs, reset, reset as resetBlogs } from "../../features/blogsSlice";


export default function HomePage() {
  // const [blogs, setBlogs] = useState();
  const dispatch = useDispatch();
  const [categories, setCategories] = useState();

  const {
    blogs,
    isError: isBlogsError,
    isSuccess: blogsSuccess,
    isLoading: isLoadingBlogs,
    message: blogsMessage,
  } = useSelector((state) => state.blogs);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // const blogsRes = await blogService.fetchBlogs();
        dispatch(fetchBlogs());
        const categoryRes = await categoryService.fetchCategories();
        setCategories(categoryRes.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
    return () => {
      dispatch(resetBlogs());
    }
  }, [dispatch]);

  
  if (isLoadingBlogs) {
    return <Loading/>
  }

  return (
    <>
      <Navbar />
      <Heading />
      <div className="container">
        <SubHeading subHeading={"Recent blog posts"} />
        <BlogGrid blogPosts={blogs} />
        <CategoriesList categories={categories} />
        <Footer />
      </div>
    </>
  );
}
