import React from "react";
import PropType from "prop-types";

import BlogItem from "../BlogItem";

import "./index.css";

export default function BlogGrid({ blogPosts }) {
  if (!blogPosts && !blogPosts?.length) {
    return null;
  }
  return (
    <>
      <div className="container-fluid py-2">
        <div className="row">
          <div className="col-12 col-md-6 mb-3">
            {blogPosts.length > 0 && (
              <BlogItem imageOrientation="top" index={0} blog={blogPosts[0]} />
            )}
          </div>
  
          <div className="col-12 col-md-6">
            {blogPosts.length > 1 && (
              <div className="mb-3">
                <BlogItem imageOrientation="left" index={1} blog={blogPosts[1]} />
              </div>
            )}
  
            {blogPosts.length > 2 && (
              <div className="">
                <BlogItem index={2} blog={blogPosts[2]} />
              </div>
            )}
          </div>
        </div>
  
        {blogPosts.length > 3 && (
          <div className="row">
            <div className="col-12 mb-3">
              <BlogItem index={3} blog={blogPosts[3]} />
              <Categories categories={blogPosts[3]?.categories} />
            </div>
          </div>
        )}
      </div>
    </>
  );
}

BlogGrid.prototype = {
  blogPost: PropType.array.isRequired,
};
