import React from "react";
import PropTypes from "prop-types";
import { useDispatch } from "react-redux";

import BlogItem from "../BlogItem";

import "./index.css";

import {
  setDeleteBlog,
  setEditBlog,
} from "../../features/blogsSlice";
import { useNavigate } from "react-router-dom";

export default function BlogList({ blogs }) {

  const user = JSON.parse(localStorage.getItem("user"));

  const nav = useNavigate();
  const dispatch = useDispatch();

  if (!blogs && !blogs?.length) {
    return null;
  }

  const onBlogEdit = (blog) => {
    dispatch(setEditBlog(blog));
  }

  const onBlogDelete= (blog) => {
    dispatch(setDeleteBlog(blog));
  }

  return (
    <div className="container">
      <div className="row">
      {blogs.map((blog, index) => {
        return (
          <div key={index} className="col-12 col-sm-12 col-md-6 col-lg-4 mb-4">
            <div className="card h-100" onClick={() => nav(`/blog/${blog.id}`)}>
              <div >
          <BlogItem
            key={index}
            index={index}
            blog={blog}
            imageOrientation="top"
            onBlogEdit={user._id === blog.author.id ? onBlogEdit : null}
            onBlogDelete={user._id === blog.author.id ? onBlogDelete : null}
          />
          </div>
          </div>
          </div>
        );
      })}
      </div>
    </div>
  );
}

BlogList.prototype = {
  blogs: PropTypes.array.isRequired,
};
