import React, { useEffect, useState} from "react";
import { useParams, Link } from "react-router-dom";

import "bootstrap/dist/css/bootstrap.min.css";

import Navbar from "../../components/Navbar";
import Heading from "../../components/Heading";
import BlogList from "../../components/BlogList";
import Footer from "../../components/Footer";
import Loading from "../../components/Loading";
import CategoriesList from "../../components/CategoriesList";

import categoriesService from "../../services/categoriesService";
import blogService from "../../services/blogService";
import AddEditBlogModal from "../../components/AddEditBlogModal";
import SuccessToast from "../../components/SuccessToast";
import ErrorToast from "../../components/ErrorToast";

import "../../App.css";
import "./index.css";




// Week 1: Import the blogPosts and categories from the dummy-data.json file


export default function BlogsPage() {

  const user = true;

  let { categoryIdPassed } = useParams();
  const [ blogs, setBlogs] = useState();
  const [ categories, setCategories] = useState([]);

  const [ loading, setLoading ] = useState(true);
  const [isError, setIsError] = useState(false);
  const [isSuccess, setIsSuccess] = useState(false);
  const [message, setMessage] = useState("");

  const [addBlog, setAddBlog] = useState({
    image: "",
    title: "",
    description: "",
    categories: [],
    content: [],
    authorId: "",
  });
  const [editBlog, setEditBlog] = useState({
    image: "",
    title: "",
    description: "",
    categories: [],
    content: [],
    authorId: "",
  });
  const [updateBlog, setUpdateBlog] = useState({
    image: "",
    title: "",
    description: "",
    categories: [],
    content: [],
    authorId: "",
  });
  const [createBlog, setCreateBlog] = useState({
    image: "",
    title: "",
    description: "",
    categories: [],
    content: [],
    authorId: "",
  });

  const onAddBlog = () => {
    setAddBlog({
      image: "",
      title: "",
      description: "",
      categories: [],
      content: [],
      authorId: user?.id,
    });
  };

  const AddBlog = () => {
    return (
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <p className="page-subtitle">Blog Posts</p>
        {user && (
          <button
            style={{ margin: "16px" }}
            type="button"
            className="btn btn-outline-secondary"
            onClick={onAddBlog}
          >
            Add Blog Post
          </button>
        )}
      </div>
    );
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        let blogPosts;
        if (!categoryIdPassed) {
          blogPosts = await blogService.getBlogs();
        } else {
          blogPosts = await blogService.getBlogsByCategoryId(categoryIdPassed);
        }
        
        const categoriesJSON = await categoriesService.fetchCategories();
        setCategories(categoriesJSON.data);
        setBlogs(blogPosts.data);
        setIsSuccess(true);
        setMessage(blogPosts.message);
        setLoading(false);
      } catch (error) {
        setIsError(true);
        setMessage(error.message || error);
        setLoading(false);
      }
    };
    fetchData();
  }, [categoryIdPassed]);

  const resetSuccess = () => {
    setIsSuccess(false);
    setMessage("");
  }

  const resetError = () => {
    setIsError(false);
    setMessage("");
  }



   const CategoriesList = () => {
    

     return categories.map((category) => {
       return categoryIdPassed === category.id.toString() ? (
        
         <Link
           key={category.id}
           to={"/blogs/category/"+category.id}
           style={{ color: "blue" }}
           onClick={() => setLoading(true)}
         >
           <p key={category.id}>{category.title}</p>
         </Link>
       ) : (
         <Link
           key={category.id}
           to={"/blogs/category/"+category.id}
           style={{ color: "black" }}
           onClick={() => setLoading(true)}
         >
           <p key={category.id}>{category.title}</p>
         </Link>
       );
     });
  }

   if (loading) {
     return <Loading/>
   }

   return (
     <>
       <Navbar />
       <div className="container">
         <Heading />
         <div className="scroll-menu">
           <CategoriesList categories={categories}/>
         </div>
         <AddBlog />
         <AddEditBlogModal
           addBlog={addBlog}
           editBlog={editBlog}

           createBlog={createBlog}
           updateBlog={updateBlog}
         />
         <BlogList blogPosts={blogs} />
       </div>
       <Footer />
       <SuccessToast
         show={isSuccess}
         message={message}
         onClose={resetSuccess}
       />
       <ErrorToast show={isError} message={message} onClose={resetError} />
     </>
   );
}
